import ExpenseItem from "./components/ExpenseItem";

function App() {
  const style = {
    width: '100vw',
    height: '100vh',
    background: 'linear-gradient(#8fb9a8, #fefad4, #fcd0ba, #f1828d, #765d69)', // Set your desired background color
    height: '100vh', // Ensure it covers the full height of the viewport
    margin: 0, // Remove default margin
    padding: 0, // Remove default padding
    position: 'relative'
  };

  const shell = {
    // backgroundColor: '#dddad8',
    padding: '20px',
    margin: '50px',
    // 'border-radius': '12px'
  };

  const overlayStyle = {
    content: "''",
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    background: 'rgba(0, 0, 0, 0.3)', // Adjust the opacity as needed
    pointerEvents: 'none', // Allows clicks to pass through to elements below
  };

  const expenses = [
    {
      id: 'e1',
      title: 'Toilet Paper',
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: 'e3',
      title: 'Car Insurance',
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: 'e4',
      title: 'New Desk (Wooden)',
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];

  return (
    <div style={style}>
      <div style={overlayStyle}></div>
      <h2>Expense App</h2>
      <div style={shell}>
      <ExpenseItem 
        title={expenses[0].title} 
        date={expenses[0].date} 
        amount={expenses[0].amount}
      />
      <ExpenseItem 
        title={expenses[1].title} 
        date={expenses[1].date} 
        amount={expenses[1].amount}
      />
      <ExpenseItem 
        title={expenses[2].title} 
        date={expenses[2].date} 
        amount={expenses[2].amount}
      />
      </div>
    </div>
  );
}

export default App;
